import React from 'react';
import { DndContext, PointerSensor, useSensor, useSensors, DragOverlay } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy, arrayMove } from '@dnd-kit/sortable';
import { restrictToWindowEdges } from '@dnd-kit/modifiers';
import KanbanColumn from './Kanb';
import SortableTask from './SortableTask';
import './TaskKanban.css';

const TaskKanban = ({ tasks, onEdit, onDelete, onStatusChange }) => {
  const [activeTask, setActiveTask] = React.useState(null);
  
  // Group tasks by status
  const [columns, setColumns] = React.useState({
    backlog: {
      id: 'backlog',
      title: 'Backlog',
      taskIds: []
    },
    active: {
      id: 'active',
      title: 'Active',
      taskIds: []
    },
    'under-review': {
      id: 'under-review',
      title: 'Under Review',
      taskIds: []
    },
    completed: {
      id: 'completed',
      title: 'Completed',
      taskIds: []
    }
  });

  // Setup sensors for drag detection
  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 5, // 5px movement required before drag starts
      },
    })
  );

  // Initialize columns with tasks
  React.useEffect(() => {
    const newColumns = {
      backlog: {
        id: 'backlog',
        title: 'Backlog',
        taskIds: []
      },
      active: {
        id: 'active',
        title: 'Active',
        taskIds: []
      },
      'under-review': {
        id: 'under-review',
        title: 'Under Review',
        taskIds: []
      },
      completed: {
        id: 'completed',
        title: 'Completed',
        taskIds: []
      }
    };
    
    tasks.forEach(task => {
      const taskId = task._id || task.id;
      
      // Map API status to our columns
      let columnId;
      switch(task.status) {
        case 'completed':
          columnId = 'completed';
          break;
        case 'under-review':
          columnId = 'under-review';
          break;
        case 'backlog':
          columnId = 'backlog';
          break;
        case 'active':
        default:
          columnId = 'active';
          break;
      }
      
      // Make sure column exists before pushing
      if (newColumns[columnId]) {
        newColumns[columnId].taskIds.push(taskId);
      } else {
        // Fallback to active if column doesn't exist
        newColumns.active.taskIds.push(taskId);
      }
    });
    
    setColumns(newColumns);
  }, [tasks]);

  // Find task by ID helper
  const getTaskById = React.useCallback((id) => {
    return tasks.find(task => (task._id || task.id) === id);
  }, [tasks]);

  // Handle drag start
  const handleDragStart = (event) => {
    const { active } = event;
    setActiveTask(getTaskById(active.id));
  };

  // Handle drag end - update columns and call API
  const handleDragEnd = (event) => {
    const { active, over } = event;
    
    if (!over) return;
    
    const taskId = active.id;
    const sourceColumnId = Object.keys(columns).find(
      colId => columns[colId].taskIds.includes(taskId)
    );
    const destinationColumnId = over.id.includes('column:') 
      ? over.id.replace('column:', '') 
      : sourceColumnId;
    
    // If task was dropped in a different column
    if (sourceColumnId !== destinationColumnId) {
      setColumns(prevColumns => {
        // Remove from source column
        const sourceTaskIds = [...prevColumns[sourceColumnId].taskIds];
        const taskIndex = sourceTaskIds.indexOf(taskId);
        sourceTaskIds.splice(taskIndex, 1);
        
        // Add to destination column
        const destinationTaskIds = [...prevColumns[destinationColumnId].taskIds];
        destinationTaskIds.push(taskId);
        
        return {
          ...prevColumns,
          [sourceColumnId]: {
            ...prevColumns[sourceColumnId],
            taskIds: sourceTaskIds
          },
          [destinationColumnId]: {
            ...prevColumns[destinationColumnId],
            taskIds: destinationTaskIds
          }
        };
      });
      
      // Map column id back to API status
      let newStatus;
      switch(destinationColumnId) {
        case 'completed':
          newStatus = 'completed';
          break;
        case 'under-review':
          newStatus = 'under-review';
          break;
        case 'backlog':
          newStatus = 'backlog';
          break;
        case 'active':
        default:
          newStatus = 'active';
          break;
      }
      
      // Call API to update task status
      onStatusChange(taskId, newStatus);
    }
    
    setActiveTask(null);
  };

  // Find column for a task
  const getColumnForTask = (taskId) => {
    return Object.keys(columns).find(
      columnId => columns[columnId].taskIds.includes(taskId)
    );
  };

  return (
    <DndContext
      sensors={sensors}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
      modifiers={[restrictToWindowEdges]}
    >
      <div className="kanban-board">
        {Object.values(columns).map(column => (
          <KanbanColumn
            key={column.id}
            id={column.id}
            title={column.title}
            taskIds={column.taskIds}
            tasks={tasks}
            getTaskById={getTaskById}
            onEdit={onEdit}
            onDelete={onDelete}
          />
        ))}
      </div>
      <DragOverlay>
        {activeTask ? (
          <div className="task-card dragging">
            <div className="task-card-header">
              <h3 className="task-card-title">{activeTask.title || activeTask.text}</h3>
            </div>
            {activeTask.description && (
              <p className="task-card-description">{activeTask.description}</p>
            )}
          </div>
        ) : null}
      </DragOverlay>
    </DndContext>
  );
};

export default TaskKanban;
