import React from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

const SortableTask = ({ id, task, onEdit, onDelete }) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  // Determine priority class
  const getPriorityClass = () => {
    if (!task.priority) return '';
    return `priority-${task.priority.toLowerCase()}`;
  };

  // Stop propagation to prevent drag when clicking buttons
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    e.preventDefault();
    action();
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      className={`task-card sortable-task ${isDragging ? 'dragging' : ''} ${getPriorityClass()}`}
    >
      {/* Make the header the drag handle */}
      <div className="task-card-header" {...attributes} {...listeners}>
        <h4 className="task-card-title">{task.title || task.text}</h4>
      </div>
      
      {task.description && (
        <p className="task-card-description">{task.description}</p>
      )}
      
      <div className="task-card-footer">
        {task.category && (
          <span className="task-category">
            <i className="fa fa-tag"></i> {task.category}
          </span>
        )}
        
        {task.dueDate && (
          <span className="task-due-date">
            <i className="fa fa-calendar"></i> {new Date(task.dueDate).toLocaleDateString()}
          </span>
        )}
        
        {/* Move actions to footer and ensure they don't interfere with dragging */}
        <div className="task-actions">
          <button 
            className="task-action-button edit" 
            onClick={(e) => handleActionClick(e, () => onEdit(task))}
          >
            <i className="fa fa-edit"></i>
          </button>
          <button 
            className="task-action-button delete" 
            onClick={(e) => handleActionClick(e, () => onDelete(id))}
          >
            <i className="fa fa-trash"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default SortableTask;
