import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import Task from '../components/Task';
import TaskKanban from '../components/TaskKanban';
import { Helmet } from 'react-helmet';
import api from '../services/api';
import './TaskPage.css';

// Number of ms to debounce task fetching
const DEBOUNCE_DELAY = 300;

// Define valid task statuses (ideally from a shared constants file)
const VALID_TASK_STATUSES_ARRAY = ['backlog', 'active', 'under-review', 'completed', 'archived'];
const DEFAULT_ACTIVE_STATUS = 'active'; // Status to revert to when un-completing

const TASK_STATUSES = {
  BACKLOG: 'backlog',
  ACTIVE: 'active',
  UNDER_REVIEW: 'under-review',
  COMPLETED: 'completed',
  ARCHIVED: 'archived',
};

const TaskPage = () => {
  const [tasks, setTasks] = useState([]);
  const [taskTitle, setTaskTitle] = useState(''); // Renamed from taskText
  const [taskDescription, setTaskDescription] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [currentTaskId, setCurrentTaskId] = useState(null);
  const [filter, setFilter] = useState('all'); // 'all', 'active', 'completed'
  const [mainLoading, setMainLoading] = useState(true); // For initial/full task list loading
  const [isSubmitting, setIsSubmitting] = useState(false); // For form submissions/actions
  const [error, setError] = useState(null); // Now an object { message: '', details: '' }
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [category, setCategory] = useState('');
  const [priority, setPriority] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('createdAt:desc');
  const [userProfile, setUserProfile] = useState(null);
  const [loggingOut, setLoggingOut] = useState(false);
  const [isFetching, setIsFetching] = useState(false);
  const [viewMode, setViewMode] = useState('kanban');
  const history = useHistory();
  const timeoutRef = useRef(null);

  const parseApiError = (err, defaultMessage) => {
    let message = defaultMessage;
    let details = '';
    if (err.status === 429) {
      message = 'Too many requests. Please try again in a moment.';
    } else if (err.data) {
      message = err.data.message || message;
      details = err.data.detailedMessage || (typeof err.data.error === 'string' ? err.data.error : '');
      if (!details && err.data.error && typeof err.data.error === 'object') {
        details = Object.values(err.data.error).map(e => e.message || e).join('; ');
      }
    } else if (err.message && !String(err.message).toLowerCase().includes('status code')) {
        message = err.message;
    }
    return { message, details };
  };

  useEffect(() => {
    let isMounted = true;
    const getUserProfile = async () => {
      try {
        const profile = await api.user.getProfile();
        if (isMounted) setUserProfile(profile);
      } catch (err) {
        console.error('Error fetching user profile:', err);
        if (isMounted && (err.status === 401 || err.message?.includes('unauthorized'))) {
          history.push('/login');
        }
      }
    };
    getUserProfile();
    return () => { isMounted = false; };
  }, [history]);

  const getStatusQueryParam = useCallback(() => {
    if (filter === 'completed') return 'completed';
    if (filter === 'active') return 'active'; // Backend handles 'active' as non-completed/non-archived
    return ''; // 'all'
  }, [filter]);

  const queryParams = useMemo(() => ({
    page,
    limit: viewMode === 'kanban' ? 100 : 10,
    status: getStatusQueryParam(),
    category: category || undefined,
    search: searchQuery || undefined,
    sortBy,
    priority: priority || undefined,
    dueDate: dueDate || undefined,
  }), [page, getStatusQueryParam, category, searchQuery, sortBy, priority, dueDate, viewMode]);

  const executeFetch = useCallback(async (showLoading = true) => {
    if (isFetching) return;
    setIsFetching(true);
    if (showLoading) setMainLoading(true);
    setError(null);
    try {
      const response = await api.tasks.getTasks(queryParams);
      setTasks(response.tasks || []);
      setTotalPages(response.pagination?.pages || 1);
    } catch (err) {
      console.error('Error fetching tasks:', err);
      const parsedError = parseApiError(err, 'Failed to load tasks. Please try again.');
      if (err.status === 401) history.push('/login');
      else setError(parsedError);
    } finally {
      if (showLoading) setMainLoading(false);
      setIsFetching(false);
    }
  }, [queryParams, history, isFetching]); // Added isFetching

  useEffect(() => {
    if (timeoutRef.current) clearTimeout(timeoutRef.current);
    timeoutRef.current = setTimeout(() => executeFetch(), DEBOUNCE_DELAY);
    return () => { if (timeoutRef.current) clearTimeout(timeoutRef.current); };
  }, [executeFetch]); // executeFetch is the dependency

  const resetForm = () => {
    setTaskTitle('');
    setTaskDescription('');
    setCategory('');
    setPriority('');
    setDueDate('');
    setIsEditing(false);
    setCurrentTaskId(null);
  };

  const handleAddTask = async () => {
    if (taskTitle.trim() === '') {
        setError({message: "Task title cannot be empty."});
        return;
    }
    setIsSubmitting(true);
    setError(null);
    try {
      const taskPayload = {
        title: taskTitle,
        description: taskDescription,
        category: category || undefined,
        priority: priority || undefined,
        dueDate: dueDate || undefined,
      };
      if (isEditing && currentTaskId) {
        await api.tasks.updateTask(currentTaskId, taskPayload);
      } else {
        await api.tasks.createTask(taskPayload);
      }
      resetForm();
      executeFetch(false); // Refresh without main loading spinner
    } catch (err) {
      console.error('Error saving task:', err);
      setError(parseApiError(err, 'Failed to save task. Please try again.'));
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (!taskId) return;
    const originalTasks = [...tasks];
    setTasks(prevTasks => prevTasks.filter(task => (task._id || task.id) !== taskId));
    setIsSubmitting(true); // Or a specific deleting state
    setError(null);
    try {
      await api.tasks.deleteTask(taskId);
      // executeFetch(false); // Refresh, or rely on optimistic update if no pagination/sorting changes
    } catch (err) {
      console.error('Error deleting task:', err);
      setTasks(originalTasks); // Revert optimistic update
      setError(parseApiError(err, 'Failed to delete task. Please try again.'));
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleEditTask = (task) => {
    setIsEditing(true);
    setCurrentTaskId(task._id || task.id);
    setTaskTitle(task.title || '');
    setTaskDescription(task.description || '');
    setCategory(task.category || '');
    setPriority(task.priority || '');
    setDueDate(task.dueDate ? new Date(task.dueDate).toISOString().split('T')[0] : '');
    window.scrollTo({ top: 0, behavior: 'smooth' }); // Scroll to form
  };

  const handleToggleComplete = async (taskId) => {
    if (!taskId) return;
    const originalTasks = [...tasks];
    const taskIndex = originalTasks.findIndex(t => (t._id || t.id) === taskId);
    if (taskIndex === -1) return;

    const updatedStatus = originalTasks[taskIndex].status === TASK_STATUSES.COMPLETED
        ? DEFAULT_ACTIVE_STATUS // Revert to a default active status
        : TASK_STATUSES.COMPLETED;

    setTasks(prevTasks =>
      prevTasks.map(task =>
        (task._id || task.id) === taskId ? { ...task, status: updatedStatus } : task
      )
    );
    // No separate submitting state, as it's a quick toggle
    setError(null);
    try {
      await api.tasks.toggleTaskStatus(taskId); // Backend handles the exact status logic
      // executeFetch(false); // Optionally refresh if backend returns more changes
    } catch (err) {
      console.error('Error updating task status:', err);
      setTasks(originalTasks); // Revert
      setError(parseApiError(err, 'Failed to update task status. Please try again.'));
    }
  };

  const handleStatusChange = async (taskId, newStatus) => {
    if (!taskId || !VALID_TASK_STATUSES_ARRAY.includes(newStatus)) {
      console.error('Invalid task ID or status for update', { taskId, newStatus });
      setError({ message: 'Invalid data for status update. Please refresh.' });
      executeFetch();
      return;
    }

    const originalTasks = [...tasks];
    const taskToUpdate = originalTasks.find(t => (t._id || t.id) === taskId);

    if (!taskToUpdate) {
      console.error('Original task not found for status update:', taskId);
      setError({ message: 'Task not found for status update. Please refresh.' });
      executeFetch();
      return;
    }
    
    // Optimistic Update for the main tasks array
    setTasks(prevTasks =>
      prevTasks.map(task =>
        (task._id || task.id) === taskId ? { ...task, status: newStatus } : task
      )
    );
    setError(null);

    try {
      await api.tasks.updateTask(taskId, { status: newStatus });
      // TaskKanban handles its own visual update. If successful, tasks state is already updated.
      // executeFetch(false); // Consider if a silent refresh is needed for other derived data.
    } catch (err) {
      console.error(`Error updating task ${taskId} to status ${newStatus} via API:`, err);
      setTasks(originalTasks); // Revert optimistic update on main tasks array
      setError(parseApiError(err, `Failed to update status for task "${taskToUpdate.title || taskId}". Please try again.`));
      // The TaskKanban component might need a way to be told to revert its state too,
      // or rely on the next full executeFetch if one is triggered.
      // For now, if error occurs, a full refresh is safest if Kanban state is complex.
      executeFetch();
    }
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    setPage(1);
    executeFetch();
  };

  const handleClearFiltersAndSearch = () => {
    setFilter('all');
    setCategory('');
    setPriority('');
    setDueDate('');
    setSearchQuery('');
    setSortBy('createdAt:desc');
    setPage(1);
    // executeFetch will be triggered by useEffect on queryParams change
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages && newPage !== page) {
      setPage(newPage);
      // executeFetch will be triggered
    }
  };

  const handleLogout = async () => {
    setLoggingOut(true);
    setError(null);
    try {
      await api.auth.logout();
      history.push('/login');
    } catch (err) {
      console.error('Logout error:', err);
      setError(parseApiError(err, 'Logout failed. Please try again.'));
      setLoggingOut(false);
    }
  };

  const activeTaskCount = useMemo(() => {
    // Counts tasks that are not 'completed' or 'archived'
    return tasks.filter(task => ![TASK_STATUSES.COMPLETED, TASK_STATUSES.ARCHIVED].includes(task.status)).length;
  }, [tasks]);
  
  const completedTaskCount = useMemo(() => {
    return tasks.filter(task => task.status === TASK_STATUSES.COMPLETED).length;
  }, [tasks]);


  return (
    <>
      <Helmet>
        <title>TaskFlow - Manage Your Tasks</title>
        <meta name="description" content="Create, organize, and track your tasks efficiently with TaskFlow." />
      </Helmet>

      <div className="container">
        <div className="header task-header">
          {/* ... Header JSX ... */}
          <div className="logo-section">
            <h1 className="app-logo">TaskFlow</h1>
          </div>
          {userProfile && (
            <div className="user-controls">
              <div className="user-info">
                <span className="welcome-text">Welcome, {userProfile.name}</span>
                <div className="user-actions">
                  <a href="/profile" className="profile-link"> {/* Consider Link from react-router-dom */}
                    <i className="fa fa-user-circle"></i> My Profile
                  </a>
                  <button className="logout-button" onClick={handleLogout} disabled={loggingOut}>
                    <i className="fa fa-sign-out"></i> {loggingOut ? 'Logging out...' : 'Logout'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="content-area">
          <h1 className="page-title">Task Management</h1>

          {error && (
            <div className="error-message">
              <div>
                <p><i className="fa fa-exclamation-circle"></i> {error.message}</p>
                {error.details && <small className="error-details">{error.details}</small>}
              </div>
              <button onClick={() => setError(null)}>Dismiss</button>
            </div>
          )}

          <div className="card task-form-card">
            <h2 className="card-title">
              <i className="fa fa-tasks"></i> {isEditing ? 'Edit Task' : 'Add New Task'}
            </h2>
            <div className="form-group">
              <label htmlFor="taskTitle">Task Title:</label>
              <input
                id="taskTitle" type="text" value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
                placeholder="What needs to be done?" className="form-input" required
              />
            </div>
            <div className="form-group">
              <label htmlFor="taskDescription">Description (optional):</label>
              <textarea
                id="taskDescription" value={taskDescription}
                onChange={(e) => setTaskDescription(e.target.value)}
                placeholder="Add details about this task..." className="form-textarea" rows="3"
              />
            </div>
            <div className="form-row">
              <div className="form-group half-width">
                <label htmlFor="taskCategory"><i className="fa fa-tag"></i> Category:</label>
                <input
                  id="taskCategory" type="text" value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  placeholder="e.g. Work, Personal" className="form-input"
                />
              </div>
              <div className="form-group half-width">
                <label htmlFor="taskPriority"><i className="fa fa-flag"></i> Priority:</label>
                <select
                  id="taskPriority" value={priority}
                  onChange={(e) => setPriority(e.target.value)} className="form-select"
                >
                  <option value="">Select Priority</option>
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="taskDueDate"><i className="fa fa-calendar"></i> Due Date (optional):</label>
              <input
                id="taskDueDate" type="date" value={dueDate}
                onChange={(e) => setDueDate(e.target.value)} className="form-input"
              />
            </div>
            <div className="button-row">
              <button
                className="button primary-button" onClick={handleAddTask}
                disabled={isSubmitting || !taskTitle.trim()}
              >
                <i className={`fa ${isSubmitting ? 'fa-spinner fa-spin' : (isEditing ? 'fa-save' : 'fa-plus-circle')}`}></i>
                {isSubmitting ? (isEditing ? 'Updating...' : 'Adding...') : (isEditing ? 'Update Task' : 'Add Task')}
              </button>
              {isEditing && (
                <button className="button secondary-button" onClick={resetForm} disabled={isSubmitting}>
                  <i className="fa fa-times-circle"></i> Cancel
                </button>
              )}
            </div>
          </div>

          <div className="view-toggle-container">
            {/* ... View Toggle JSX ... */}
            <div className="view-toggle">
              <button
                className={`view-button ${viewMode === 'list' ? 'active' : ''}`}
                onClick={() => setViewMode('list')}
                disabled={mainLoading || isFetching}
              >
                <i className="fa fa-list"></i> List View
              </button>
              <button
                className={`view-button ${viewMode === 'kanban' ? 'active' : ''}`}
                onClick={() => setViewMode('kanban')}
                disabled={mainLoading || isFetching}
              >
                <i className="fa fa-columns"></i> Kanban View
              </button>
            </div>
          </div>

          <div className="card filter-card">
            {/* ... Filter Controls JSX ... */}
            <div className="filter-controls">
              <form onSubmit={handleSearchSubmit} className="search-form">
                <div className="search-input-wrapper">
                  <input
                    type="text" placeholder="Search tasks..."
                    value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)}
                    className="form-input search-input"
                  />
                  <button type="submit" className="search-button" disabled={isFetching}>
                    <i className="fa fa-search"></i>
                  </button>
                </div>
              </form>
              <div className="advanced-filters">
                <div className="filter-group">
                  <label htmlFor="filterStatus">Status:</label>
                  <select
                    id="filterStatus" value={filter}
                    onChange={(e) => setFilter(e.target.value)}
                    className="form-select filter-select"
                    disabled={isFetching}
                  >
                    <option value="all">All Tasks</option>
                    <option value="active">Active</option>
                    <option value="completed">Completed</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label htmlFor="filterSort">Sort By:</label>
                  <select
                    id="filterSort" value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="form-select filter-select"
                    disabled={isFetching}
                  >
                    <option value="createdAt:desc">Newest First</option>
                    <option value="createdAt:asc">Oldest First</option>
                    <option value="title:asc">A-Z</option>
                    <option value="title:desc">Z-A</option>
                    <option value="priority:desc">Priority (High-Low)</option>
                    <option value="priority:asc">Priority (Low-High)</option>
                    <option value="dueDate:asc">Due Date (Earliest)</option>
                    <option value="dueDate:desc">Due Date (Latest)</option>
                  </select>
                </div>
                <button className="clear-filters-button" onClick={handleClearFiltersAndSearch} disabled={isFetching}>
                  <i className="fa fa-times"></i> Clear Filters
                </button>
              </div>
            </div>
          </div>

          <div className="task-content-container">
            {mainLoading ? (
              <div className="loading-container">
                <div className="loading-spinner">
                  <i className="fa fa-spinner fa-spin"></i>
                  <p>Loading tasks...</p>
                </div>
              </div>
            ) : (
              <>
                {viewMode === 'kanban' ? (
                  <div className="kanban-container">
                    <TaskKanban
                      tasks={tasks}
                      onEdit={handleEditTask}
                      onDelete={handleDeleteTask}
                      onStatusChange={handleStatusChange}
                      // Pass valid statuses for column generation if TaskKanban needs it
                      // columnOrder={['backlog', 'active', 'under-review', 'completed']}
                    />
                  </div>
                ) : (
                  <div className="list-container">
                    <div className="task-stats">
                      <span className="task-count">
                        <i className="fa fa-check-circle"></i> {tasks.length} tasks total
                      </span>
                      <span className="task-count active">
                        <i className="fa fa-clock-o"></i> {activeTaskCount} active
                      </span>
                      <span className="task-count completed">
                        <i className="fa fa-check"></i> {completedTaskCount} completed
                      </span>
                    </div>
                    <div className="task-list">
                      {tasks.length === 0 && !searchQuery && filter === 'all' ? (
                         <div className="empty-state">
                           <i className="fa fa-clipboard-list empty-icon"></i>
                           <p>No tasks yet. Add one above to get started!</p>
                         </div>
                       ) : tasks.length === 0 ? (
                        <div className="empty-state">
                          <i className="fa fa-tasks empty-icon"></i>
                          <p>No tasks found with the current filters.</p>
                          <button className="button secondary-button" onClick={handleClearFiltersAndSearch}>
                            Clear Filters
                          </button>
                        </div>
                      ) : (
                        tasks.map(task => (
                          <Task
                            key={task._id || task.id}
                            task={task}
                            onToggle={() => handleToggleComplete(task._id || task.id)}
                            onEdit={() => handleEditTask(task)}
                            onDelete={() => handleDeleteTask(task._id || task.id)}
                          />
                        ))
                      )}
                    </div>
                    {totalPages > 1 && viewMode === 'list' && ( // Show pagination only for list view if kanban loads all
                      <div className="pagination">
                        {/* ... Pagination JSX ... */}
                        <button
                          className="pagination-button"
                          onClick={() => handlePageChange(page - 1)}
                          disabled={page === 1 || isFetching}
                        >
                          <i className="fa fa-chevron-left"></i> Previous
                        </button>
                        <div className="pagination-info">Page {page} of {totalPages}</div>
                        <button
                          className="pagination-button"
                          onClick={() => handlePageChange(page + 1)}
                          disabled={page === totalPages || isFetching}
                        >
                          Next <i className="fa fa-chevron-right"></i>
                        </button>
                      </div>
                    )}
                  </div>
                )}
              </>
            )}
          </div>

          <div className="quick-help-section">
            {/* ... Quick Help JSX ... */}
          </div>
        </div>

        <footer className="app-footer">
          <p> {new Date().getFullYear()} TaskFlow App. All rights reserved.</p>
        </footer>
      </div>
    </>
  );
};

export default TaskPage;
