import React from 'react';
import { useDroppable } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy } from '@dnd-kit/sortable';
import SortableTask from './SortableTask';

const KanbanColumn = ({ id, title, taskIds, tasks, getTaskById, onEdit, onDelete }) => {
  const { setNodeRef, isOver } = useDroppable({
    id: `column:${id}`,
  });

  return (
    <div 
      className={`kanban-column ${isOver ? 'drop-active' : ''}`}
      ref={setNodeRef}
      data-column-id={id}
    >
      <div className="kanban-column-header">
        <h3 className="kanban-column-title">{title}</h3>
        <span className="task-count">{taskIds.length}</span>
      </div>
      
      <div className="kanban-column-content">
        {taskIds.length === 0 ? (
          <div className="empty-column">
            <p>No tasks</p>
          </div>
        ) : (
          <SortableContext 
            items={taskIds} 
            strategy={verticalListSortingStrategy}
          >
            {taskIds.map(taskId => {
              const task = getTaskById(taskId);
              if (!task) return null;
              
              return (
                <SortableTask
                  key={taskId}
                  id={taskId}
                  task={task}
                  onEdit={() => onEdit(task)}
                  onDelete={() => onDelete(taskId)}
                />
              );
            })}
          </SortableContext>
        )}
      </div>
    </div>
  );
};

export default KanbanColumn;
