import React from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

const SortableTask = ({ id, task, onEdit, onDelete }) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  // Format due date for display
  const formatDueDate = (dateString) => {
    if (!dateString) return null;
    
    const date = new Date(dateString);
    const today = new Date();
    const isOverdue = date < today && task.status !== 'completed';
    
    // Format: Jan 5, 2023
    const formattedDate = date.toLocaleDateString('en-US', {
      month: 'short', 
      day: 'numeric',
      year: 'numeric'
    });
    
    return (
      <div className={`task-due-date ${isOverdue ? 'overdue' : ''}`}>
        <i className="fa fa-calendar"></i>
        <span>{formattedDate}</span>
        {isOverdue && task.status !== 'completed' && (
          <span className="overdue-badge">Overdue</span>
        )}
      </div>
    );
  };

  // Generate CSS classes for priority
  const getPriorityClass = (priority) => {
    switch(priority?.toLowerCase()) {
      case 'high': return 'high';
      case 'medium': return 'medium';
      case 'low': return 'low';
      default: return '';
    }
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      {...attributes}
      {...listeners}
      className={`task-card ${isDragging ? 'dragging' : ''}`}
    >
      <div className="task-card-header">
        <h3 className="task-card-title">{task.title || task.text}</h3>
        <div className="task-actions">
          <button className="task-action-button edit" onClick={onEdit}>
            <i className="fa fa-pencil"></i>
          </button>
          <button className="task-action-button delete" onClick={onDelete}>
            <i className="fa fa-trash"></i>
          </button>
        </div>
      </div>
      
      {task.description && (
        <p className="task-card-description">{task.description}</p>
      )}
      
      <div className="task-card-meta">
        {task.category && (
          <div className="task-category">
            <i className="fa fa-tag"></i>
            <span>{task.category}</span>
          </div>
        )}
        
        {task.priority && (
          <div className={`task-priority ${getPriorityClass(task.priority)}`}>
            <i className="fa fa-flag"></i>
            <span>{task.priority}</span>
          </div>
        )}
        
        {task.dueDate && formatDueDate(task.dueDate)}
      </div>
    </div>
  );
};

export default SortableTask;
